const slider = document.querySelector('.example-item-image-slider');
        const afterImage = document.querySelector('.example-item-after-image');

        slider.addEventListener('input', (event) => {
            const value = event.target.value;
            afterImage.style.clipPath = `inset(0 0 0 ${value}%)`; // Управляем видимой частью изображения "После"
        });